
public class Produto {
	String nome;
	int valor;

	public Produto(String nome, int valor) {
		// TODO Auto-generated constructor stub
		this.nome= nome;
		this.valor = valor;
	}

	public String getNome() {
		return nome;
	}

	public int getValor() {
		// TODO Auto-generated method stub
		return valor;
	}

}
